import axios from 'axios'

import React, {Component} from "react";
import {Form} from 'react-bootstrap'

axios.defaults.xsrfHeaderName = "X-CSRFToken";
axios.defaults.xsrfCookieName = "csrftoken";


export default class Contacts extends Component {


    constructor(props) {
    super(props);
    this.state = {
      name: '',
        description: '',
        email: '',
        phone: ''


    };

    this.handleChange = this.onChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


onChange = (e) => this.setState({ [e.target.name]: e.target.value });
    async getData(){

}
handleSubmit(event)  {

     axios({
            method: "POST",
            url: `http://127.0.0.1:8000/api/topback/`,
            data: {
              name: this.state.name,
              email: this.state.email,
              description: this.state.description,
              phone: this.state.phone
            },
         headers: {
            "X-CSRFToken": "csrftoken",

         },

        }).then(res => {
                 this.state.data = res.data
                console.log(this.state.data)




    }).catch(function (error) {
        console.log(error);

    });
    event.preventDefault();



}

render() {

  return (

      <main id="main">


          <div className="breadcrumbs" data-aos="fade-in">
              <div className="container">
                  <h2>Contact Us</h2>
                  <p>Est dolorum ut non facere possimus quibusdam eligendi voluptatem. Quia id aut similique quia
                      voluptas sit quaerat debitis. Rerum omnis ipsam aperiam consequatur laboriosam nemo harum
                      praesentium. </p>
              </div>
          </div>

          <section id="contact" className="contact">
              <div data-aos="fade-up">
                  <iframe style={{border:'0', width: '100%' , height: '350px'}}
                          src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d12097.433213460943!2d-74.0062269!3d40.7101282!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0xb89d1fe6bc499443!2sDowntown+Conference+Center!5e0!3m2!1smk!2sbg!4v1539943755621"
                          frameBorder="0" allowFullScreen></iframe>
              </div>

              <div className="container" data-aos="fade-up">

                  <div className="row mt-5">

                      <div className="col-lg-4">
                          <div className="info">
                              <div className="address">
                                  <i className="bi bi-geo-alt"></i>
                                  <h4>Location:</h4>
                                  <p>A108 Adam Street, New York, NY 535022</p>
                              </div>

                              <div className="email">
                                  <i className="bi bi-envelope"></i>
                                  <h4>Email:</h4>
                                  <p>info@example.com</p>
                              </div>

                              <div className="phone">
                                  <i className="bi bi-phone"></i>
                                  <h4>Call:</h4>
                                  <p>+1 5589 55488 55s</p>
                              </div>

                          </div>

                      </div>

                      <div className="col-lg-8 mt-5 mt-lg-0">

                          <Form >
                              <div className="row">
                                  <div className="col-md-6 form-group">
                                      <input type="text" name="name" className="form-control" id="name"
                                             placeholder="Your Name" onChange = {this.onChange} value = {this.state.name} required/>
                                  </div>
                                  <div className="col-md-6 form-group mt-3 mt-md-0">
                                      <Form.Control type="email" className="form-control" name="email" id="email"
                                             placeholder="Your Email"onChange = {this.onChange} value = {this.state.email} required/>
                                  </div>
                              </div>
                              <div className="form-group mt-3">
                                  <Form.Control type="text" className="form-control" name="phone" id="phone"
                                         placeholder="Subject" onChange = {this.onChange} value ={this.state.phone}required/>
                              </div>
                              <div className="form-group mt-3">
                                  <Form.Control as = 'textarea' className="form-control" name="description" rows="5"
                                  onChange = {this.onChange} value = {this.state.description} placeholder="Message"
                                            required />
                              </div>
                              <div className="my-3">
                                  <div className="loading">Loading</div>
                                  <div className="error-message"></div>
                                  <div className="sent-message">Your message has been sent. Thank you!</div>
                              </div>
                              <div className="text-center">
                                  <button  onClick = {this.handleSubmit} type="submit">Send Message</button>
                              </div>
                          </Form>

                      </div>

                  </div>

              </div>
          </section>


      </main>

);
}

}
